	CF.Documento AS NFe,
	CF.Serie AS Serie,
	CONVERT(DATE,GETDATE()) AS Data_Emissao,
	'009486' AS Romaneio_Fornecedor,
	CP.CGC_Cliente AS Codigo_Fornecedor,
	CP.Razao_Nome_Cliente AS Razao_Social_Fornecedor,
